// funcao scrollsuave
function initScrollSuave() {
  // funcao scrollar
  function scrollToSection(event) {
    event.preventDefault();
    const href = event.currentTarget.getAttribute("href");
    const section = document.querySelector(href);

    const topo = section.offsetTop;
    window.scrollTo({
      top: topo - 72,
      behavior: "smooth"
    });
  }

  // Pega os elementos e adiciona o escutador
  const linksInternos = document.querySelectorAll('body a[href^="#"]');
  linksInternos.forEach(link => {
    link.addEventListener("click", scrollToSection);
  });
}
initScrollSuave();
// Fim funcao scrollsuave

// animacoes

// funcao animar ao scroll
function initAnimacaoScroll() {
  const sections = document.querySelectorAll(".js-scroll");

  if (sections.length) {
    const windowHalf = window.innerHeight * 0.5;

    function animaScroll() {
      sections.forEach(section => {
        const sectionTop = section.getBoundingClientRect().top - windowHalf;
        if (sectionTop < 0) {
          section.classList.add("ativo");
        }
      });
    }

    window.addEventListener("scroll", animaScroll);
  }
}
initAnimacaoScroll();
// fim funcao animar ao scroll

